sys_variable_value.value:
var gr = new GlideRecord('x_51117_accessctrl_profile_tasks_relation');
gr.addQuery('profile', current.profile);
gr.addQuery('phase', '1');
gr.query();

workflow.info("Numero de tareas: " + gr.getRowCount());

while(gr.next())
{
	
	var template = new GlideRecord('x_51117_accessctrl_template');
	template.addQuery('sys_id', gr.template);
	template.query();
	workflow.info("Numero de templates: " + template.getRowCount());
	
	while(template.next())
	{
		var task = new GlideRecord('x_51117_accessctrl_access_control_task');
	    task.initialize();
	    task.short_description = template.name + ' ' + template.short_description;
		workflow.info("short description: " + template.short_description);
		task.description = template.description;
		task.assignment_group = template.assignment_group;
		task.assigned_to = template.assigned_to;
		task.parent = current.sys_id;
	    task.insert();
	}
	
}

